import Cocoa
import AVFoundation

class ThirdNSWindowController: NSWindowController {
    
    
    @IBOutlet weak var thirdTableView: NSTableView!
    @IBOutlet weak var thirdTitleWindow: NSTextField!
    @IBOutlet weak var thirdImageWindow: NSImageView!

    @IBOutlet var thirdTextViewWindow: NSTextView!
    var data: MenuData?
    convenience init() {
        self.init(windowNibName: "ThirdNSWindowController")
    }
    
    func settData(data: MenuData) {
        self.data = data
        self.window!.title = "Phần cứng máy Mac"
        
        
    }
    
    func selectedGiup() -> DetailStr?{
        let selectedRow = self.thirdTableView!.selectedRow
        if selectedRow >= 0 && selectedRow <= data!.detailData.count {
            return data!.detailData[selectedRow]
        }
        return nil
    }
    
    func upDateDetailInfor(giupMacS: DetailStr?) {
        var title = ""
        var imagePath = ""
        var detail = ""
        if let giupMacss = giupMacS {
            title = giupMacss.title
            imagePath = giupMacss.imagePath
            detail = giupMacss.detail
        }
        thirdTitleWindow.stringValue = title
        thirdImageWindow.image = NSImage(named: imagePath)
        thirdTextViewWindow.string = detail
        self.thirdTextViewWindow.textStorage!.font = NSFont(name: "Helvetica Neue", size: 16)
        
        
    }
    
}

extension ThirdNSWindowController: NSTableViewDataSource {
    func numberOfRowsInTableView(tableView: NSTableView) -> Int {
        return data!.detailData.count
    }
    func tableView(tableView: NSTableView, viewForTableColumn tableColumn:NSTableColumn?, row: Int) -> NSView? {
        let cellView: NSTableCellView = tableView.makeViewWithIdentifier(tableColumn!.identifier, owner: self) as! NSTableCellView
        let detailStr = data!.detailData[row]
        cellView.imageView!.image = NSImage(named: detailStr.imagePath)
        cellView.textField!.stringValue = detailStr.title
        return cellView
    }
    func tableViewSelectionDidChange(notification: NSNotification) {
        if let selectedGiupTable = selectedGiup() {
            upDateDetailInfor(selectedGiupTable)
        }
        
    }
}
extension ThirdNSWindowController: NSTableViewDelegate {
    
}

class SecondNSWindowController: NSWindowController {
    
    @IBOutlet weak var secondTableView: NSTableView!
    @IBOutlet weak var secondTitleRow: NSTextField!
    @IBOutlet weak var secondImage: NSImageView!
   
    @IBOutlet var secondTextView: NSTextView!
    
    
    var data: MenuData?
    convenience init() {
        self.init(windowNibName: "SecondNSWindowController")
    }
    
    func settData(data: MenuData) {
        
        self.data = data
        self.window!.title = "Hệ điều hành OSX"
    }
    
    func selectedGiup() -> DetailStr?{
        let selectedRow = self.secondTableView.selectedRow
        if selectedRow >= 0 && selectedRow <= data!.detailData.count {
            return data!.detailData[selectedRow]
        }
        return nil
    }
    
    func updateDetailInfor(giupMacS: DetailStr?) {
        var title = ""
        var imagePath = ""
        var detail = ""
        if let giupMacss = giupMacS {
            title = giupMacss.title
            imagePath = giupMacss.imagePath
            detail = giupMacss.detail
        }
        
        secondTitleRow.stringValue = title
        secondImage.image = NSImage(named: imagePath)
        secondTextView.string = detail
        secondTextView.textStorage?.font = NSFont(name: "Helvetica Neue", size: 16)
        
    }

   
    
    
    
}

extension SecondNSWindowController: NSTableViewDataSource {
    
    func numberOfRowsInTableView(tableView: NSTableView) -> Int {
        
        return data!.detailData.count
    }
    
    func tableView(tableView: NSTableView, viewForTableColumn tableColumn:NSTableColumn?, row: Int) -> NSView? {
        let cellView: NSTableCellView = tableView.makeViewWithIdentifier(tableColumn!.identifier, owner: self) as! NSTableCellView
            let detailStr = data!.detailData[row]
            cellView.imageView!.image = NSImage(named: detailStr.imagePath)
            cellView.textField!.stringValue = detailStr.title
        return cellView
        
    }
    
    func tableViewSelectionDidChange(notification: NSNotification) {
        let selectedGiuptt = selectedGiup()
        updateDetailInfor(selectedGiuptt)
    }
}

extension SecondNSWindowController: NSTableViewDelegate {
    
}

class MasterNSViewController: NSViewController {
    private var secondNSWindowController: SecondNSWindowController?
    private var thirdNSWindowController: ThirdNSWindowController?
    var menuData = Array<MenuData>()
    var backgroundMusicP : AVAudioPlayer!
    var k:Bool = true
    
    @IBOutlet weak var backGroundMS: NSButton!
    @IBOutlet weak var buttonOSX: NSButton!
    @IBOutlet weak var buttonMayMac: NSButton!
   override func viewDidLoad() {
        super.viewDidLoad()
        playBackgroundMusic("Lounge Ambient Track Sound Clips from Orange Free Sound.mp3")
        let giupMacData = MenuData(category: "macos")
        let giupMacData2 = MenuData(category: "mayMac")
        giupMacData.addData("Hệ Điều Hành Mac", imagePath: "os-x.jpg", detail: "Đối với những người dùng Windows đã quen, nay chuyển sang Mac thì đó là một sự thay đổi lớn, không phải nói là thay đổi hoàn toàn. \n Một giao diện hoàn toàn khác, Taskbar thì nằm phía trên và phía dưới là 1 thanh dock với các ứng dụng nằm trên đó. Hoàn toàn màn hình desktop không có một folder hay chương trình nào. \n Bắt đầu, click vào Finder (Finder bên Mac thì chính là My Computer bên Windows), tất cả các mục đều nằm trong Finder này. Có một phần mềm luôn chạy cùng với Mac, đó là Finder, đây chính là phần cơ bản nhất của Mac.  \n Finder có chức năng quản lý files, folders, quản lý các công việc, thao tác ... của bạn. \n Desktop là một phần trong Finder, và trong Finder cũng có 1 folders là Desktop. \n Khi ta mở một thư mục nào đó thì đó chính là Finder: \n Phía bên trái cửa sổ là Sidebar, nơi hiển thị các thông tin, Devices, Places (ở đây các bạn có thể bỏ các thư mục các bạn thích như là Dock) \n Nên ta có thể cho hiện đường dẫn của File or Folders – tại thanh bar phía dưới – bằng cách: thanh Menu – View -> Show Path Bar. \n =>Preference của Finder (phím tắt là Command + , [tất cả các ứng dụng nào khi ấn tổ hợp này là sẽ vào Preference của ứng dụng đó dùng để tinh chỉnh nhiều thứ, giống như là Options bên Windows vậy]). \n Và để coi thông tin của File hay Folder thì ta click chuột phải vào file hay folder đó, chọn Get Info, hoặc tổ hợp phím Command + I, nếu muốn xem 1 lúc nhiều file hoặc folder gộp lại thì thêm Alt (option) vậy tổ hợp phím là option + command + I")
        giupMacData.addData("Bảng điều khiển “Control Panel”", imagePath: "Control Panel.png", detail: "Nếu như người dùng Windows có bảng điều khiển Control Panel để tùy chỉnh hệ thống thì OS X cũng có mục tương đương nhưng gọi là System Preferences. Bạn vào mục này bằng cách nhấn biểu tượng Táo phía trên cùng bên trái giao diện chính OS X và chọn System Preferences.")
        giupMacData.addData("Sử dụng Dock", imagePath: "Dock.png", detail: "Không có Start Menu và không có Taskbar, giao diện chính của Mac hoàn toàn được điều hành bởi chân đế Dock nằm phía dưới, nơi để những ứng dụng bạn hay dùng nhất. Để xem toàn bộ danh sách ứng dụng nhấn biểu tượng Launchpad (thứ 2 từ trái sang).Để thêm một ứng dụng vào Dock, bạn chỉ cần kéo thả biểu tượng ứng dụng đó xuống chỗ muốn để. Để bỏ một ứng dụng nào đó ra khỏi Dock, bạn chỉ cần kéo bỏ biểu tượng ra vùng trống.Dock cũng có chức năng tương tự như thanh Taskbar trên Windows, nơi lưu trữ những cửa sổ ứng dụng đang mở được thu gọn xuống.Dock mặc định có sẵn 2 stack phía bên trái thùng rác. Stack là alias link đến một thư mục nào đó, ta có thể thêm hoặc bớt các stack (thao tác giống như thêm một ứng dụng vào Dock), được chia ra bởi 1 hình vạch trắng phần này để chứa gọi nôm na Stack là các folder chứa các file. Khi Kich chuột phải vào một stack, có một menu hiện ra và ta có thể chỉnh các thông số của stack.")
        giupMacData.addData("Top Menu", imagePath: "Top Menu.png", detail: "Ở ngay đầu bên trái là logo quả táo App. Logo này là cố định, dù bạn đang chạy phần mềm nào thì icon này cũng hiện ở đây. Và đây cũng là menu để truy cập nhanh vào các chức năng cần thiết của hệ điều hành. Một số chức năng quan trọng là:\n About This Mac: Dùng để check thông tin, cấu hình máy, xem dung lượng ổ cứng.\n Software Update: cập nhật phiên bản mới nhất cho các phần mềm và hệ điều hành trong máy. \n Force Quit: tắt tất cả các phần mềm đang chạy (trong trường hợp phần mềm đó bị treo, không tắt bình thường được).Kế tiếp chính là tên phần mềm đang chạy và mình đang sử dụng. Khi kick và tên phần mềm thì sẽ xuất hiện một menu (bất cứ phần mềm nào cũng có menu này). Chúng ta có thể coi thông tin về phần mềm này và quan trọng nhất là truy cập được vào Preferences của phần mềm đó.\n Phần còn lại chính là các menu của phần mềm. Mac có một điểm đăc biệt khác win đó là các phần mềm đang chạy, dù đang ở vị trí nào trên màn hình thì menu của nó cũng hiện ở trên Top menu này (khi ta chọn một phần mềm nào đó thì đồng thời menu của nó cũng hiện hiện Top menu ở vị trí này) phía bên phải của Top Menu \n Ngoài cùng phía bên phải chính là Spotlight - chiếc kính lúp kỳ diệu: có chứ năng search, máy tính, tra từ điển,tìm kiếm từ khóa nhanh…... Nếu bạn chưa biết nó là gì thì hãy đọc nhé. Tại đây nè: Các icon từ phải qua trái lần lượt là: Spotlight, Date time, pin, volume, tắt mở wifi, tắt mở Bluetooth và cuối cùng là Time Machine.Ta có thể cài thêm iStat menus để thay thế cho các icon mặc định và thêm một số các tính năng khác như thông tin về nhiệt độ máy, thông tin ram, tốc độ quạt, tốc độ mạng download,upload...\n Để tinh chỉnh như Đổi mật khẩu, Users,Desktop, Screen Saver, Độ phân giải màn hình, Trackpad, Chuột, Wifi, Bảo mệt dữ liệu, font chữ, bàn phím Anh hay Việt, Dock…….tất cả đều nằm trong mục Preferences (Alias hình bánh răng cưa ngay phía dưới dock hoặc Quả Táo góc trái trên cùng và -> System Preferences.")
        giupMacData.addData("Mission control", imagePath: "Mission control.png", detail: "Ở các phiên bản mac trước (< 10.7 thì gọi là Expóse & Spaces). Bảng điều khiển hiển thị đủ các cửa sổ đang mở, một đặc trưng của nền tảng Mac. Các các cửa sổ thể hiện trên màn hình. Để giúp việc chuyển đổi cửa sổ làm việc một cách hiệu quả bạn hãy thiết lập nó cho phù hơp với mình nhất.\n Active Screen Corners: phần này cho ta lựa chọn việc thể hiện trên màn hình sẽ thế nào khi ta di chuyển chuột đến 4 góc của màn hình. Trong hình minh họa trên, ở góc trái bên dưới mình chọn là All Windows. Có nghĩa là mỗi khi mình di chuyển vào góc đó thì tất cả các cửa sổ sẽ được sắp xếp lại để mình cùng 1 lúc có thể thấy hết các cửa sổ đó trên màn hình và có thể chọn cửa sổ nào mình muốn cho nó hiện lên trên cùng để làm việc.\n  Keyboard and mouse Shortcut(Expóse): Tương tự như bên trên nhưng thay vì ta di chuyển chuột đến 4 góc thì ta chọn phím tắt. Mặc định là các phím. F4 để sếp tất cả các ứng dụng lên màn hình, F3 để hiện ứng dụng bạn đang làm việc rõ lên, các ứng dụng khác sẽ mờ đi, Fn + F11 để hiện ra màn hình desktop các cửa sổ ứng dụng sẽ chạy ra 4 cạnh.\n Dashboard: Phần này quy định phím, nút để mở và tắt Dashboard., Fn + F12 thi là Dashboard, là một khu vực chứa nhiều tiện ích nhỏ như là Widgets bên Windows. Hiện đã có hàng ngàn ứng dụng nhỏ khác nhau cho Dashboard mà bạn có thể tải về từ chợ Appstore.")
        giupMacData.addData("Trackpad", imagePath: "track.png", detail: "Có thể dùng 1 ngón để click chuột, 2 ngón để trượt, xoay, phóng to thu nhỏ và 3 ngón để qua bài, qua hình. Vào đây sẽ thấy và có thể bật lên, tùy vào máy của bạn và phiên bản hệ điều hành mà bạn sẽ thấy ít hay nhiều ngón. \n   Nếu dùng hệ điều hành Mac trên dòng máy Mac, bạn có trong tay những thao tác điều khiển vô cùng độc đáo và linh hoạt bằng bàn cảm ứng Trackpad hay cả bằng chuột cảm ứng…\n “Lăn chuột”:trên Windows người ta lăn chuột để xem hết nội dung một bài báo hay một đoạn văn bản dài thì với Mac, bạn có thể dùng 2 ngón tay vuốt lên xuống.\n Duyệt trang:Bạn chuyển qua lại giữa những trang web đã mở trên Safari, hay giữa những đoạn văn bản khác nhau bằng cách dùng 2 ngón tay vuốt sang 2 bên.\nZoom thông minh:Tính năng Smart Zoom cho phép bạn dùng 2 ngón tay nhấn đúp vào bàn cảm ứng để tự động phóng to một phần trang nội dung. Hãy nhấn đúp bằng 2 ngón tay lần nữa để thu nhỏ lại.\nZoom “bằng tay”:Nếu như bạn đã quen dùng các loại smartphone thì sẽ không lạ lẫm gì thao tác này. Dùng 2 ngón tay tách ra xa nhau trên bàn Trackpad để phóng to dần nội dung đang xem và “cấu” lại để thu nhỏ.\n Mở Mission Control:dùng 3 ngón tay vuốt lên trên bàn Trackpad. Để đóng Mission Control lại, hãy dùng 3 ngón tay vuốt xuống.\n Duyệt các ứng dụng mở toàn màn hình:Bạn di chuyển qua lại giữa các ứng dụng toàn màn hình hoặc các khu ứng dụng đang mở bằng cách dùng 4 ngón tay vuốt sang 2 bên.\n Mở Launchpad: Launchpad là nơi có danh sách của tất cả các ứng dụng cài trong máy bạn.Để mở Launchpad, hãy “cấu” như thao tác zoom nhưng bằng ngón cái với 3 ngón khác. Để đóng Launchpad, hãy “cấu” ngược lại một lần nữa.\n Về Desktop:Bạn về màn hình chính Desktop từ bất kỳ đâu bằng thao tác tách giống như zoom nhưng là 5 ngón cấu ra.\n Nghiên cứu:(Chuột phải) Dùng 2 ngón tay nhấn vào bàn Trackpad, bạn sẽ có một danh sách lựa chọn để tra cứu về từ đang nằm dưới con trỏ hoặc đang được bôi đen.\n Vào Notification Center: Bạn vào trung tâm thông báo cập nhật Notification Center bằng cách dùng 2 ngón tay vuốt vào từ mép phải của Trackpad. Để rời Notification Center, hãy vuốt ngược lại.")
        giupMacData.addData(" Language & Text", imagePath: "Language & Text.png", detail: "Ta có thể chỉnh ngôn ngữ của hệ thống (mặc định là Eng, hay chọn ngôn ngữ tiếng mẹ đẻ Việt Nam vs bản Mac 10.9 trở đi). Ngoài ra có thể chỉnh định dạng của số, đồng hồ, ngày tháng trong tab Formats. Quan trọng nhất ở phần này là Input Sources , tại đây ta có thể kick hoạt các bộ gõ có sẵn của mac để phục vụ nhu cầu của mình. Với các máy chạy hệ điều hành Mac mới (>= 10.7), bản gõ Tiếng Việt kiểu Telex và VNI đã có sẵn trong máy, các bạn chỉ cần chọn vào thôi (sau đó nó sẽ đc tự động kích hoạt và để thay đổi kiểu đánh, các bạn chọn vào icon ngay sát cục pin trên thanh Top Menu phía phải).")
        giupMacData.addData("Energy Saver ", imagePath: "Energy Saver.png", detail: "Thiết lập về chế độ sử dụng pin để pin được tốt hơn.Ở mục này có thể chỉnh thời gian sử dụng máy, thời gian sleep, hoặc chọn cái có sẵn trong mục: Optimization. Khi kick vào Schedule... ta có thể đặt thời gian mở máy và tắt máy (hoặc sleep)")
        giupMacData.addData("Keyboard", imagePath: "Keyboard.png", detail: "Mặc định của Mac là khi bạn nhấn F1, F2 ... thì nó không ra F1, F2 mà nó là phím chức năng, như chỉnh sáng tối, âm lượng, đèn bàn phím.... Để F1 chính là F1 thì ta chọn vào Use all F1, F2, ect. keys as standard function keys. Khi đó muốn chỉnh sáng tối, âm lượng... thì ta dùng Fn + F1 (hoặc Fn + F2 .....). \n  Illuminate keyboard in low light conditions: nếu mục này được chọn thì tức là chức năng tự động bật đèn và chỉnh độ sáng của đèn bàn phím đã được bật lên.")
        giupMacData.addData("Accessibility(Universal Access):", imagePath: "Accessibility.png", detail: "VoiceOver: Khi bạn thấy tự nhiên cái máy bạn lại phát ra những tiếng nói khó hiểu (thực ra là chức năng phát âm định hướng dành cho người mù)\n  Zoom: Khi bạn bật On chức năng này thì bạn có thể phóng to thu nhỏ màn hình bằng cách nhấn alt + command + - (hoặc alt + command + \n  Enable access for assistive devices: bật chức năng phát âm của mac. Như khi cài translateIT sẽ yêu cầu bạn chọn cái này để có thể chạy chế độ phát âm.")
        giupMacData.addData("Cách cài đặt ứng dụng, game cho Mac", imagePath: "installApplication.jpg", detail: "File setup cho Mac là DMG, các bạn khi down về sẽ thấy (EXE là cho Windows, nhưng file nào có đuôi là *.exe thì không chạy đc trên Mac nhé). \n   Khi click vào file DMG đó, sẽ có 2 trường hợp cho các bạn cài:\n  Một là nó sẽ ra file *.mpkg, đối với file này, các bạn click vô, cài bình thường, chọn continue như là next bên Windows vậy đó, khi cài xong app nó sẽ nằm trong mục Application thôi.vidu như Photoshop, Office... \n   Hai là nó sẽ ra như trên hình, bạn chỉ việc kéo nó vào mục Application ngay đó hoặc dưới thanh Dock, mọi thứ hoàn tất sau khi nó copy xong, bạn chỉ việc click vô xài thôi,vidu như: Yahoo, Firefox...")
        giupMacData.addData("Sự khác nhau giữa Mac và Windows", imagePath: "macPC.png", detail: "Việc đầu tiên sau khi chạy hệ điều hành Mac ra lần đầu tiên hay ngay sau khi mới cài lại hệ điều hành cho máy: đó chính là cho chạy phần mềm Software Update, bằng cách: Kick vào hình trái táo ở góc trên trái của màn hình và chọn Software Update. Công việc này nhằm giúp cho máy có thể cập nhật được những version của phần mềm, hệ điều hành mới nhất mà Táo cập nhật và cung cấp cho chúng ta. Điều này mang lại sự hoạt động ổn định cho máy, bớt nóng, và hơn hết là chúng ta có một cái gì đó luôn luôn mới mẻ trong máy tính, tạo sự hứng thú khi sử dụng. \n   LƯU Ý CẦN THIẾT:\n  Những phần mềm chạy trên windows thì không chạy được trên Mac và ngược lại. Phần mềm trên win có dạng *.exe còn trên mac định dạng của của nó là *.app. Và định dạng file cài các app của Mac là *.dmg còn Win là *.exe. \n   Thư mục Application là nơi chứa các phần mềm trong Mac (giống như Programs bên win).Restart Mode, Sleep va Shutdown: Mac có chức năng sleep rất hay. Khi dùng chức năng này thì tất cả các cửa sổ, công việc mình đang thực hiện đều được lưu lại, và bạn có thể kích hoạt lại máy tính bằng cách kick vào phím chuột hoặc Spacebar. Chức năng này ít hao pin và có thể gập máy lại trong quá trình di chuyển. \n    lời khuyên: những bạn nào có công việc hay di chuyển, thì mỗi khi đóng máy, đừng nên tắt máy làm gì, vì như thế khi bật lên sẽ ảnh hưởng đến thời gian của người dùng xài Mac, chỉ cần đóng máy cho máy ngủ bằng cách gập màn hình, khi nào ngưng sử dụng trong 1 thời gian dài thì shut down, điều này hoàn toàn không ảnh hưởng gì đến máy, nên các bạn cứ yên tâm nhé.")
        giupMacData.addData("Các phím tắt hữa dụng khi sử dụng máy Mac", imagePath: " các phím tắt hữa dụng.png", detail: "Có hai loại phím tắt: phím tắt hệ thống và phím tắt ứng dụng. Bạn có thể tìm thấy các phím tắt hệ thống thông qua System Preferences - Keyboard và phím tắt ứng dụng thông qua thanh menu của ứng dụng đó.\n Nên in danh sách các phím tắt và dán lên chỗ ngồi làm việc để có thể dễ dàng nhìn thấy, thực hành và ghi nhớ chúng.\n COMMAND SPACE Mở Splotlight \n COMMAND OPTION SPACE cửa sổ Splotlight \n SHIFT COMMAND DELETE xả thùng rác \n COMMAND N Cửa sổ Finder mới \n SHIFT COMMAND N Thư mục mới \n COMMAND I Xem thông tin \n COMMAND F Tìm kiếm \n COMMAND E Huỷ gắn ổ đĩa \n COMMAND J Tuỳ chọn xem \n COMMAND K Kết nối máy chủ \n SHIFT COMMAND A Mở thư mục Application \n SHIFT COMMAND C Liệt kê ổ đĩa \n COMMAND UP Mở thư mục trên \n COMMAND DOWN Mở thư mục hiện hành \n COMMAND C Sao chép file\n COMMAND V Dán file \n OPTION COMMAND V Di chuyển file \n COMMAND TAB Di chuyển giữa các ứng dụng \n COMMAND ` Cửa sổ tiếp theo của cùng ứng dụng \n COMMAND TAB Chọn tab tiếp theo \n SHIFT COMMAND TAB Về tab trước \n COMMAND SHIFT 3 Chụp toàn màn hình \n COMMAND SHIFT CONTROL 3 Lưu ảnh toàn màn hình vào clipboard \n COMMAND SHIFT CONTROL 4 Lưu ảnh khu vực vào clipboard \n SHIFT COMMAND SPACE 4 Chụp cửa sổ (Lưu trên Desktop) \n COMMAND OPTION 8 Bật/tắt tính năng phóng to \n OPTION COMMAND = Phóng to \n OPTION COMMAND - Thu nhỏ \n CONTROL CUỘN CHUỘT phóng to thu nhỏ \n COMMAND OPTION SHIFT DELETE Xoá sạch thùng rác tức thì \n SPACE Xem nhanh ảnh hoặc văn bản \n COMMAND OPTION ESC Mở hộp thoại Force Quit \n COMMAND OPTION ESC SHIFT Force Quit App hiện hành (nhấn giữ hơn 3 giây \n COMMAND LEFT Tới đầu dòng văn bản \n COMMAND RIGHT Tới cuối dòng văn bản \n COMMAND UP Về đầu văn bản \n COMMAND DOWN Về cuối văn bản \n OPTION LEFT Tới từ trước văn bản \n OPTION RIGHT Tới từ sau văn bản \n OPTION UP Tới đoạn văn bản trước \n OPTION DOWN Tới đoạn văn bản sau \n --- COMMAND LEFT SHIFT Bôi đen tới đầu dòng văn bản \n COMMAND RIGHT SHIFT Bôi đen tới cuối dòng văn bản \n COMMAND UP SHIFT Bôi đen về đầu văn bản \n COMMAND DOWN SHIFT Bôi đen về cuối văn bản \n OPTION LEFT SHIFT Bôi đen tới từ trước văn bản \n OPTION RIGHT SHIFT Bôi đen tới từ sau văn bản \n OPTION UP SHIFT Bôi đen tới đoạn văn bản trước \n OPTION DOWN SHIFT Bôi đen tới đoạn văn bản sau \n COMMAND A Bôi đen tất cả \n COMMAND SHIFT L Tìm trên web cái đã bôi đen \n COMMAND SHIFT Y Mở Stickies cái đã bôi đen \n COMMAND OPTION EJECT Sleep \n COMMAND CONTROL EJECT Khởi động lại \n COMMAND OPTION CONTROL EJECT Tắt máy \n CONTROL SHIFT EJECT Tắt màn hình \n CONTROL EJECT Hiển thị hộp thoại tắt máy \n COMMAND N Cửa sổ mới \n COMMAND O Mở file \n COMMAND W Đóng cửa sổ \n COMMAND S Lưu \n COMMAND SHIFT S Lưu ở dạng \n COMMAND P In ấn \n COMMAND Q Thoát \n COMMAND , Tuỳ chỉnh app \n COMMAND H Ẩn cửa sổ \n COMMAND OPTION H Ẩn tất cả cửa sổ \n COMMAND Bảng font \n COMMAND SHIFT C Bnagr mầu \n COMMAND SHIFT / Trợ giúp \n COMMAND CONTROL F Toàn màn hình.")
        giupMacData.addData("Hướng dẫn cài Windows thông qua Bootcamp dành cho Mac", imagePath: "Windows on Mac.png", detail: "Nói về những điều hay của Mac, nhưng số đông người dùng vẫn xài Windows quen rồi nên việc chạy 2 hệ điều hành song song là điều dĩ nhiên cho công việc cũng như môi trường quen thuộc, thời gian cài này mất khoảng < 1h. \n   Cài Windows cho Mac có 2 loai khác nhau. Một là cài thông qua Bootcamp, chạy Windows song song với Mac, hai là Parallel(Wmware) là xem như chạy 1 chương trình trong Mac vậy (có ưu điểm là có win nhanh ko phải mắc công restart máy nhưng sẽ nặng máy và hiệu năng kém hơn, không hổ trợ tối ưu hết hiệu năng mà Windows mang lại so với khởi động sang chạy hệ điều hành Win, vì thế người dùng nên cài Windows qua Bootcamp, tuy nó mất công restart nhưng tiện lợi hơn cho chúng ta sau này. \n   Hướng dẫn cài windows qua phần mềm có sẵn trong máy là Bootcamp:\n  1 - Cho đĩa cài windows vô hoặc cắm usb đã được tạo bộ cài windows cho mac vô. Khởi động trình Boot Camp Assistant: vào Applications/Ultility hoặc tại Finder nhấn tổ hợp phím Shift+Command+u. Boot Camp Assistant sẽ giúp bạn chia ổ cứng và chuẩn bị cài Windows. Tạo phân vùng ổ cứng cho Windows: Boot Camp sẽ tạo thêm một phân vùng nữa cho ổ cứng trong máy Táo của bạn. Mặc định là 20Gb cho phân vùng này, 20Gb là dung lượng tối thiểu để cài Win 7, nếu bạn cài win 7 thì bạn phải chọn nhiều hơn. Sau khi cài Windows ta có thể dùng phần mềm để có thể khai thác ổ MAC và ngược lại. \n   2 - Bắt đầu cài đặt: Sau khi đã tạp xong phân vùng cho Windows, ta bỏ đĩa cài đặt hệ điều hành Windows và nhấn Start Installation để bắt đầu cài đặt Windows. Máy tính của bạn sẽ được khởi động lại và tự động cài đặt Windows. \n   3 - Cài đặt Windows: Phải chọn đúng phân vùng với dung lượng mà bạn đã chia cho Windows để cài lên. thường thì trên ổ đĩa đó có chữ Boot Camp. Các bạn nhớ định dạng (format) lại phân vùng đó thì mới cài đặt được. Ta chọn ổ cứng rồi nhấn vào nút Format rồi cài đặt như windows bình thường. \n   4 - Cài driver cho Windows:Sau khi cài xong, các bạn thắc mắc là làm thế nào để cài Driver. Vô cùng đơn giản, để cài Driver cho Windows ta bỏ đĩa cài đặt OSX kèm theo máy của bạn vào máy để cài( đối với các máy cũ, cách hay hơn là lên trang chủ apple vô support tìm download file bootcam về cài). Xong máy sẽ tự khởi động lại và bạn sử dụng Windows như 1 máy tính hệ điều hành Windows như bình thường rồi đó. \n  5 - Chọn hệ điều hành khi khởi động: Ngay khi mở máy lên hay khởi động lại máy bạn nhấn phím Atl/Option trên bàn phím để chọn hệ điều hành mà bạn muốn máy chạy. Việc chọn này chỉ nhất thời, lần sau máy sẽ tự động chọn hệ điều hành đã được chọn mặc định, nên để mặc định là Mac, khi nào cần qua Windows thì sẽ restart lại và chọn vào Windows\n  6 - Chọn hệ điều hành mặc định: BootCamp cho phép bạn chọn hệ điều hành mặc định khi máy khởi động vào. Hoặc vừa bật máy lên bạn nhấn phím Alt/Option trên bàn phím để chọn hệ điều hành mà mình muốn máy chạy. Để chọn hệ điều hành mặc định cho máy ta làm như sau:/Trên OSX: Vào System Preferences chọn Startup Disk, chọn ổ đĩa (hđh) mà bạn muốn máy dùng mặc định, bất kể từ giờ trở về sau, khi mở máy thì máy sẽ tự động vào hệ điều hành mà bạn đã chọn khi nãy. \n   Trên Windows: Nếu bạn đang ở trên Windows bạn vào Control Panel, tìm biểu tượng Startup Disk và chọn giống như bên OSX. \n   7 - Sơ đồ phím Táo trên Windows: Phím Command sẽ là phím cửa sổ bên Windows còn lại là giống nhau. \n   8 - Gõ bỏ Windows: Để xóa bỏ một bộ Windows đã cài lên máy MAC để cài lại bộ khác hoặc không dùng Windows nữa ngừơi dùng chỉ cần vào Mac  vào Applications vào Ultility chọn BootCamp Assistant và chọn Restore startup disk to single volume. Sau khi làm bước này thì sẽ không còn một dấu hiệu nào của Windows trên Mac, ngừơi dùng nhớ sao lưu các tài liệu, ứng dụng quan trọng lại trước khi làm bước này. Hoặc còn 1 cách nữa là các bạn chỉ cần vào Disk Utility và format hay gộp 2 phần vùng (1 Mac, 1 Win) lại thành 1 là xong. \n   Một số lưu ý khi cài Windows (kinh nghiệm bản thân):\n  1. Khi cài win, máy chỉ có 1 Partition (phân vùng trong ổ cứng) thôi, nhiều hơn sẽ rất dễ báo lỗi. \n   2. Nếu chia làm 3 phân vùng: 1 Mac, 1 Win, 1 Data dùng chung thì nên:Mac: định dạng Mac Extended Journaled.Win: định dạng FAT hoặc NTFS vì Mac chỉ có định dạng Fat mà thôi NTFS phải cài. Data định dạng Mac Extended.")
        giupMacData.addData("Hướng dẫn cài lại MAC OSX", imagePath: "ReInstallMac.png", detail: "Trong quá trình sử dụng, Mac hầu như rất ít khi nhiễm virus, hầu như không có, nên đó ko phải là lý do để chúng ta cài lại máy, ở đây cài lại máy giúp chúng ta giải phóng được ổ cứng, tăng dung lượng đáng kể, xóa bỏ những phần mềm không cần thiết, nhằm tránh đụng app, gây nóng máy, và điều quan trọng là giúp cho máy vận hành 1 cách trơn tru hơn, tốt hơn, và mát hơn ..... \n   Hướng dẫn ngừơi dùng cài lại MAC OSX bằng đĩa(usb đã đuọc tạo bộ cài mac os x) 1 cách nhanh nhất:\n  1. Sao lưu dữ liệu cần thiết (lưu vào HDD hay vào đâu đấy)\n  2. Khi cài máy: chúng ta có 2 lựa chọn:\n  a. Bỏ đĩa(usb) OSX vào máy đang chạy rồi chọn Install MAC OSX và nhận Restart để bắt đầu quá trình cài đặt. Người dùng phải nhập mật mã(password) để được cài lại máy. \n   b. Bỏ(usb) đĩa vào máy, sau đó ấn nút C(hoặc Alt), máy sẽ tự động đi vào trình cài đặt hoặc ngưiof dùng chon boot vô đĩa usb cài. \n   3. Chọn lựa ngôn ngữ, chia ổ cứng...Ổ cài OSX nên để ít nhất là 30G\n  - Chọn ngôn ngữ mà bạn dùng trong quá trình cài đặt cũng như sẽ sử dụng mặc định cho HĐH sau khi cài xong. \n   - Sau khi bạn chọn ngôn ngữ và nhấn tiếp tục người dùng chọn mục Utility trên thanh công cụ bên trên trong mục này có Disk Utility dùng để chia ổ đĩa./- Sau khi máy chia ổ đĩa xong các bạn đóng mục Disk Utility lại và bắt đầu cài. \n    4. Chọn ổ cứng để cài hệ điều hành: Bạn sẽ thấy số lượng đĩa cứng bạn chia có trong bảng chọn. Hãy chọn một ổ để cài đặt. \n   Nếu bạn không muốn chia lại ổ cứng thì chọn nút Option trong mục chọn ổ đĩa và chọn cách bạn muốn cài đặt: Bạn nên chọn Erase and Install để xóa sạch ổ cứng. \n   5. Máy tự động cài đặt và khởi động lại. \n   6. Chọn quốc gia: Sau phần chào bằng nhiều ngôn ngữ khác nhau thì ta được yêu cầu nhập nước mà mình đang ở, chú ý là nhập đúng Việt Nam nếu bạn đang ở VN, bảng danh sách chọn có thể sẽ không hiện ra lựa chọn Việt Nam, bạn bấm vào nút Show all thì nó sẽ hiện ra.Việc này nên chú ý, nếu bạn chọn nước khác sẽ rất khó điền số đt và địa chỉ cho phần sau. \n   7. Chọn ngôn ngữ bàn phím: Có hai lựa chọn 1 là usa và 2 là Vietnam (cờ vn). Chúng ta chọn USA và VN luôn, gồm có VNI là Telex. \n   8. Điền thông tin cá nhân. Chú ý:\n  Area Code là mã bưu điện thành phố. VN HCM là 08, HN là 04...đây không phải là mã quốc gia 084.\nPhần điện thoại thì bạn phải đánh đúng:\nVD: 0984230489 ta đánh như sau: 98 - 4230489\nHoặc Bạn bỏ qua bước này cũng được. \n   9. Chúc mừng bạn, bạn đã thành công.Hãy bắt đầu lại với 1 chiếc máy với hệ điều hành hoàn toàn mới.")
        giupMacData.addData("Về vấn đề Pin cho MacBook Pro,Air...", imagePath: "batery.jpg", detail: "Đối với thế hệ MBP mới, Táo đã cung cấp cho pin dính liền vào với máy và không tháo ra được, vì thế chúng ta nên có 1 chế độ sử dụng hợp lý, như vậy vì pin mới có thể trâu, ít bị giảm health, và bền lâu được. \n   Khi mới mua máy về, việc đầu tiên là khi mở máy, làm những công việc cần thiết và sử dụng cho hết pin, cho máy tắt hẳn, sau đó cắm sạc hơn 8 tiếng (tốt nhất là hãy cắm qua đêm), khi pin đầy, tiếp tục cắm thêm tầm 1 -> 2h nữa cho no hẳn pin, rồi rút ra và sử dụng cho đến khi hết và tiếp tục cắm lại như lần đầu. Công việc như vậy liên tục trong 1 tuần. Việc làm này giúp pin giãn nở đều đặng và có 1 dung lượng tốt nhất. \n   Sau đó, các bạn sử dụng bình thường, theo như khuyến khích của Táo thì chúng ta nên cắm điện lúc nào có thể, khi nào thực sự ko thể cắm được thì thôi, nhưng nếu có ổ nguồn thì cứ cắm cho máy. \n   Và cứ 2 tháng, thì các bạn Calibrate (tức là xả pin) 1 lần. Cách Calibrate như sau:\n  a. Cắm sạc vào và sạc đầy\n  b. Sau khi sạc báo đầy pin, tiếp tục để pin đc sạc thêm 2 tiếng hoặc hơn. \n   c. Đối với máy đang bật, ngắt sạc ra và tiếp tục sử dụng\n  d. Khi thấy pin sắp hết, save lại công việc đang làm dở và đóng tất cảc các ứng dụng. Dùng pin cho đến khi máy sleep\n  e. Để máy sleep thêm 5 tiếng hoặc hơn. \n   f. Cắm sạc cho đến khi pin báo đầy. (Khi cắm sạc, các bạn có thể mở máy xài như bình thường, ko sao)\n  - Khi ngưng sử dụng máy trong 1 thời gian dài (hơn 6 tháng), Táo khuyên chúng ta nên để máy còn ở khoảng 50% pin. thấp hơn thì máy sẽ mất khả năng sạc và cao hơn thì sẽ giảm tuổi thọ của pin\n  - Chu kì sử dụng pin của MacBook :\n  1.Macbook, macbook pro pin rời: 300 chu kỳ\n  .Macbook Air: 750 chu kỳ\n  3.Macbook pro pin liền: 1000 chu kỳ\n  1 chu kỳ đc tính khi bạn xài đúng 100% pin của máy, VD: máy đang 100%, các bạn xài 30% và cắm sạc, sau đó máy lên lại 100% các bạn lại rút ra xài 70% thì lần trước 30% và lần này 70% nữa thì là đc 1 chu kỳ. \n   Chốt lại:\n  1. Khi mua máy mới, nhớ calibrate 1 tuần đầu tiên. \n   2. Sạc pin: sau khi pin báo đầy (khoảng 2 tiếng) nên để thêm 2 tiếng nữa pin mới thực sự đầy. \n   3.Nếu thường dùng đến pin thì calibrae mỗi 2 tháng 1 lần, ít dùng thì 1 tháng 1 lần. \n   3.Có thể sạc pin bất cứ lúc nào thuận tiện, có thể vừa xài vừa xạc (nhưng những lần đầu tiên nên để xạc khi máy ko hoạt động)\n  4.Nếu ko dùng máy trong khoảng 6 tháng, xạc pin hoặc dùng pin đến khi còn 50% --->đem cất. \n   5.kiểm tra số lần sạc Bin của mac:\n  =>quả táo -> about this mac -> system report -> power ( ở trong phần hardware) -> cycle count ( chính là số lần sạc của máy)")
        giupMacData.addData("Mac share Wifi => thiết bị khác", imagePath: "share.jpg", detail: "Các bạn vào System Preferences => Sau đó vào mục Sharing =>Chọn vào ô Internet Sharing => Chỉnh Share your connection from: Erthernet => To Computers Using: Airport (hoặc cái khác mà bạn muốn) => Sau đó chọn vào Airport Options thì sẽ hiện ra khung này: => Network Name: ........ (các bạn đặt tên tùy ý) => Channel: Automatic\nnhớ Enable nó lên thì mình sẽ set Password đc, khỏi cho ai xài chùa và chọn là 40 bit, sau đó OK, và Start là xong, máy sẽ phát wifi cho các thiết bị khác \n Khi đó, icon Wifi bên góc phải sẽ thay đổi, nếu ko thích phát nữa, các bạn chọn và Off nó đi, và nếu khi muốn bắt wifi của người khác, các bạn lại vào sharing và bỏ chọn Internet Sharing là xong")
        giupMacData.addData("Chia sẽ dữ liệu từ win", imagePath: "share data mac win.png", detail: "Khi dữ liệu của bạn đều nằm trên máy tính chạy win bạn muốn dùng mac truy cập và chỉnh sữa dữ liệu trên ổ cứng sharing của máy win thì bạn cài đặt các máy trên cùng 1 mạng lan, tốt nhất là có dây và switch gigalan. Máy tính windows của bạn phải bật sharing và set permission full control ( tức là cả đọc và ghi dữ liệu).")
        giupMacData.addData("Tăng tốc tối đa máy Mac của bạn", imagePath: "Tăng tốc tối đa máy Mac.jpg", detail: "Máy tính Mac có thể phục vụ nhiều năm mà không bị trục trặc, nhưng bạn phải biết cách giữ cho máy hoạt động tốt.Một số máy Mac sau khi dùng ít nhất 5 năm có thể bị chậm lại hoặc hoạt động không tốt, và người dùng có thể cho rằng máy của họ sắp hết hạn sử dụng.Thực ra, chỉ cần dọn dẹp lại phần mềm của máy và nâng cấp phần cứng là bạn hầu như có thể đem lại sức sống mới cho máy. Trong một số trường hợp, việc thực hiện dễ dàng hơn bạn nghĩ và không cần phải vọc đến phần bên trong của máy. Những thao tác liên quan đến sửa đổi phần mềm vẫn đơn giản đối với hầu hết mọi người. Nguyên nhân phổ biến nhất dẫn đến làm chậm các hệ thống máy tính đời cũ là ổ đĩa cứng bị đầy và không đủ bộ nhớ RAM. Nhưng may mắn là chỉ cần gắn thêm tối đa dung lượng bộ nhớ mà máy có thể hỗ trợ là vấn đề được giải quyết. Ngoài ra, bạn cần nâng cấp lên phiên bản hệ điều hành Mac mới và thay ổ đĩa cứng HDD (hard disk drive) mới có tốc độ cao hay thậm chí ổ lưu trữ thể rắn SSD (solid-state drive) nếu khả năng tài chính cho phép. Và đối với tất cả máy Mac, ngay cả các model mới nhất, quan trọng là bạn phải quản lý, dọn dẹp tập tin để hệ thống hoạt động suôn sẻ, và công việc này chẳng khó khăn gì lắm đối với đa số người dùng. Bạn chỉ cần biết rằng thư viện âm nhạc và hình ảnh có thể làm đầy ổ đĩa cứng, nhưng bạn có thể di chuyển chúng sang thiết bị lưu trữ gắn ngoài. Bên cạnh đó, bộ nhớ đệm của trình duyệt có thể sẽ bị nghẽn sau 1-2 năm sử dụng nếu bạn không thường xuyên “vệ sinh”.")
        giupMacData.addData("Thiết lập để ứng dụng tự chạy khi khởi động máy Mac", imagePath: "Thietlap.jpg", detail: "Bạn có thể thiết lập để chạy ứng dụng tự động khi khởi động máy Mac trực tiếp trên thanh Dock hay chọn trong System Preferences của Mac. Chọn ứng dụng từ thanh dock. Nếu ứng dụng mà bạn dự định cho chạy tự động đang nằm trên thanh dock của Mac, hãy nhấn chuột phải lên biểu tượng của ứng dụng đó (hay giữ phím Control và nhấn lên biểu tượng). Chọn Options và sau đó chọn Open at Login. Hoặc chọn cùng lúc nhiều ứng dụng trong System Preferences. Bạn có thể chọn nhiều ứng dụng cùng lúc và thiết lập cho chúng tự động khởi chạy cùng với HĐH. Hãy nhấn lên trình đơn Táo ở góc trên bên trái của màn hình Mac, mở System Preferences. Trong mục System, nhấn chọn Users and Groups. Bạn hãy tìm tên của người dùng hay nhóm người dùng cần thiết lập ở danh sách bên trái, chọn thẻ Login Items ở phía trên. Màn hình sẽ hiển thị danh sách các ứng dụng tự chạy khi bạn đăng nhập vào hệ thống. Nếu bạn muốn thêm hay bỏ bớt ứng dụng trong danh sách này, hãy nhấn nút + hay - ở bên dưới để mở trình Finder, chọn ứng dụng cần thêm hay bỏ bớt. Nếu muốn ứng dụng tự chạy nền khi khởi động, hãy đánh dấu chọn vào khung Hide.")
        giupMacData.addData("Sao lưu với Time Machine", imagePath: "Saoluu.png", detail: "Sao lưu dự phòng là việc cần làm thường xuyên. Nếu mới dùng máy Mac, bạn nên làm quen với chương trình tích hợp sẵn trong Mac gọi là Time Machine. Chương trình này hoạt động với máy Mac và một đĩa cứng gắn ngoài để thường xuyên lưu các tài liệu quan trọng, hình ảnh và cả tập tin hệ thống quan trọng. Ngoài việc lưu giữ các bản dự phòng của mọi tập tin, Time Machine cũng lưu giữ tình trạng hệ thống trong các ngày cụ thể, giúp bạn dễ phục hồi hoàn toàn hệ thống nếu có gì trục trặc xảy ra. Trước khi bắt đầu, bạn hãy kiểm tra xem đĩa cứng ngoài có tương thích với máy Mac không. Việc này có thể thực hiện bằng cách định dạng ổ đĩa bằng tiện ích định dạng tập tin Mac. Bạn nên dùng đĩa cứng ngoài dành riêng cho Time Machine, và máy Mac của bạn phải chưa được thiết lập để sao lưu dự phòng bằng Time Machine. \n Bước 1: Kết nối đĩa cứng ngoài. Trước tiên, hãy kết nối đĩa cứng với máy Mac qua cổng thích hợp: Thunderbolt, USB, hay FireWire, tùy theo loại đĩa cứng và máy Mac của bạn. Máy Mac sẽ nhận ra phần cứng mới thêm, và một cửa sổ bật lên sẽ gợi ý cho bạn bước tiếp theo. Cửa sổ này sẽ hỏi bạn có muốn dùng ổ đĩa để sao lưu dự phòng với Time Machine không; hãy nhấp chọn Use as Backup Disk. Quá trình sao lưu dự phòng đầy đủ sẽ khởi động ngay. Sao lưu dự phòng toàn bộ hệ thống lần đầu sẽ mất khoảng vài giờ, nhưng sao lưu dự phòng thường xuyên sẽ tốn ít thời gian hơn vì ổ đĩa chỉ cần cập nhật những gì đã có sẵn và lưu các tập tin mới. Nếu đĩa cứng luôn được gắn với máy Mac, việc sao lưu dự phòng từng giờ theo lịch chỉ mất vài giây. Theo mặc định, bạn sẽ thấy biểu tượng Time Machine trong thanh trình đơn (biểu tượng này trông giống một đồng hồ có mũi tên chạy ngược chiều). Bạn có thể ngăn biểu tượng này không xuất hiện trong khung tùy chọn hệ thống Time Machine. Bạn cũng có thể truy cập tùy chọn Time Machine qua khung tùy chọn hệ thống System Preferences. Nếu Time Machine không tự động kích hoạt, nếu bạn có nhiều tùy chọn đĩa cứng ngoài để lưu trữ sao lưu dự phòng, hay nếu bạn muốn thiết lập thiết bị lưu trữ Time Capsule không dây, hãy nhấp chọn biểu tượng Time Machine trong thanh trình đơn và một trình đơn bật lên sẽ hiện ra. Hãy chọn Open Time Machine Preferences, sau đó nhấp chọn nút Select Disk. Tìm ổ đĩa nào mà bạn muốn lưu sao lưu dự phòng vào, chọn nó và nhấp chọn Use Backup Disk. Chọn ổ đĩa bạn muốn lưu sao lưu dự phòng. \n Bước 2: Cấu hình và tùy chỉnh sao lưu dự phòng. Nếu bạn không cần lưu sao lưu dự phòng mọi thứ, bạn có thể chọn thủ công các mục để loại khỏi sao lưu dự phòng thường xuyên. Bạn chỉ nhấp chọn Options từ cửa sổ Time Machine chính, rồi chọn dấu + ở phía bên trái của hộp bật lên có đề chữ Exclude These Items from Backups. Từ đó, chọn các mục bạn muốn loại bỏ, sau đó nhấp chọn Exclude. Bạn sẽ thấy các thư mục hay từng tập tin bạn đã chọn trong danh sách loại bỏ. Bạn có thể thay đổi tùy chọn về các tập tin hay thư mục này bất kỳ lúc nào bằng cách chọn tập tin và nhấp chọn dấu trừ. Khi đĩa cứng ngoài đã đầy, Time Machine sẽ tự động xóa sao lưu dự phòng cũ để nhường chỗ cho sao lưu dự phòng mới. Theo thiết lập mặc định, bạn sẽ được thông báo khi sao lưu dự phòng cũ bị xóa, nhưng nếu không cần thông tin này, bạn có thể thay đổi thiết lập. Từ cùng trình đơn Options, bạn cũng có thể khóa các tài liệu sau khi chúng được hiệu đính. \n Bước 3: Nhớ khởi động Time Machine. Bước cuối cùng là thường xuyên sử dụng Time Machine và sao lưu dự phòng các tập tin của bạn. Nếu bạn có máy Mac để bàn, bạn có thể luôn gắn đĩa cứng ngoài. Time Machine sẽ âm thầm khởi động và thực hiện sao lưu dự phòng mỗi giờ. Bạn cũng có thể khởi động sao lưu dự phòng thủ công bằng cách nhấp vào biểu tượng Time Machine và chọn Back Up Now. Nếu bạn không luôn gắn đĩa cứng sao lưu dự phòng vào máy Mac, hãy nhớ gắn vào để sao lưu bảo vệ dữ liệu. Cũng giống như lần đầu sao lưu dự phòng toàn hệ thống, Time Machine sẽ bắt đầu chạy ngay sau khi ổ đĩa được gắn vào máy.")
        giupMacData.addData("Gỡ bỏ các biểu tượng System Preferences", imagePath: "Gobo.png", detail: "Gỡ bỏ các biểu tượng System Preferences không cần thiết trong Mac. Là cách dọn dẹp ứng dụng System Preferences của HĐH Mac bằng cách ẩn những biểu tượng mà bạn ít dùng đến. System Preferences là một ứng dụng tích hợp trong HĐH Mac, là nơi tập hợp tất cả những tùy chọn hệ thống – do đó ứng dụng này hơi hỗn độn và cần phải dọn dẹp. Trong ứng dụng System Preferences (có thể truy xuất từ thanh Dock hay thư mục Applications), bạn hãy chọn trình đơn View và chọn Customize. Hoặc bạn có thể nhấn và giữ vào phím Show All trên tiêu đề System Preferences, và sau đó chọn Customize ở dưới cùng tại trình đơn xổ xuống. Trong cả hai trường hợp, bạn sẽ thấy tất cả các biểu tượng trong Systems Preferences đều xuất hiện một ô đánh dấu checkbox nằm bên cạnh. Nếu có tùy chọn nào không cần thiết, bạn hãy bỏ chọn chúng. Chẳng hạn, nếu không cần tùy chọn MobileMe hay Parental Controls, hãy bỏ chọn chúng và nhấn nút Done trên thanh tiêu đề (hay bỏ chọn Customize từ trình đơn View). Tuy nhiên, nếu sau này bạn cần sử dụng lại các tùy chọn này thì phải làm thế nào? Đừng lo, những tùy chọn đã ẩn xuất hiện ở cả trình đơn View và trình đơn Show All sẽ xuất hiện lại khi bạn nhấn và giữ phím đó. Để bỏ ẩn một tùy chọn vĩnh viễn, hãy quay lại chế độ Customize và chọn vào ô đánh dấu tùy chọn đó trước khi nhấn nút Done.")
        giupMacData.addData("Những cách tốt nhất để chạy Windows trên máy Mac", imagePath: "Nhungcach.jpg", detail: "Windows vẫn là hệ điều hành có ưu thế hơn, nhất là trong các doanh nghiệp. Nghĩa là có lúc người dùng Mac phải cần đến hệ điều hành của Microsoft. Có thể là ứng dụng mà công ty bạn dùng chỉ có thể dùng được trên Windows, hay bạn là một nhà phát triển và cần phải thử nghiệm trang web của mình trong một trình duyệt web Windows sẵn có. Hay có thể bạn muốn chơi game trên máy tính không dành cho Mac. Dù bạn dùng Windows vì lý do gì, có nhiều cách để máy Mac của bạn thực hiện được việc này. Nếu chỉ cần chạy một ứng dụng Windows, bạn có thể dùng ứng dụng CrossOver mà không cần phải cài đặt cả hệ điều hành (HĐH) Windows. Nếu cần phải cài đặt Windows đầy đủ, phức tạp hơn, bạn vẫn còn có nhiều tùy chọn khác. Bạn có thể dùng ứng dụng Boot Camp riêng của Táo, cho phép cài đặt Windows vào một phân vùng riêng của ổ đĩa cứng. Hay bạn có thể cài đặt một trong 3 chương trình ảo hóa của hãng thứ ba là Parallels Desktop 7, VMware Fusion, hay VirtualBox. Các chương trình này cho phép bạn chạy Windows (hay một HĐH khác) như thể đang chạy một ứng dụng OS X khác. Trong số 4 tùy chọn trên, Boot Camp có hiệu năng tốt nhất. Máy Mac của bạn sử dụng được toàn bộ tài nguyên để chạy Windows. Nhưng bạn phải khởi động máy lại để dùng Boot Camp, nên không thể dùng nó cùng lúc với Mac. Điều đó có nghĩa là bạn chỉ chạy Mac hay Windows, chứ không phải cả hai cùng lúc. Còn ứng dụng VirtualBox thì miễn phí, nhưng quá trình cài đặt khá phức tạp, đôi khi dùng rất chán vì ứng dụng này thiếu những công cụ bạn cần. Như vậy còn lại ứng dụng Parallels Desktop và VMware Fusion là hai lựa chọn tốt nhất. \n Hiệu năng tổng quát \n Nếu bạn muốn chạy Windows trong một máy ảo để chơi những game không chơi được trên máy Mac, bạn sẽ phải dùng đến Parallels Desktop. Chương trình này hoạt động tốt hơn Fusion, nhất là đối với các game mới hơn. Lý do là Parallels hỗ trợ VRAM (Video RAM) nhiều hơn so với Fusion. Có thể bạn sẽ muốn các ứng dụng Windows hoạt động như các ứng dụng Mac của bạn. Trong trường hợp này cách Fusion xử lý cửa sổ có vẻ hợp lý hơn.")
        giupMacData.addData("Bắt buộc ứng dụng phải thoát ", imagePath: "Batbuoc.jpg", detail: "Trong trường hợp một ứng dụng bị treo, có thể gần như không nhận các lệnh điều khiển trong cửa sổ nữa. Khi đó bạn chỉ còn một lựa chọn là bắt buộc thoát rồi bật lại. Để thực hiện, bạn có thể nhấn Command + Alt + Ecs để mở cửa sổ Force Quit (gần như Task Manager của Windows) rồi chọn ứng dụng và nhấn Force Quit. Ngoài ra, bạn cũng có thể giữ Alt, nhấn chuột phải vào biểu tượng của ứng dụng bị treo trên thanh Dock và chọn Force Quit.Sau khi ứng dụng đã thoát, chạy lại để kiểm tra độ ổn định. Nếu máy vẫn bị treo thì khả năng phần mềm này không tương thích với phiên bản OS X bạn đang sử dụng. Điều đặc biệt là tính năng Force Quit của Mac ổn định và hiệu quả hơn rất nhiều so với Windows và cũng ít gây rắc rối lớn khiến bạn khởi động lại máy tính.")
        giupMacData.addData("Mở tập tin bằng ứng dụng chỉ định trước ", imagePath: "Motap.jpg", detail: "Nếu bạn gặp thông báo lỗi không mở được một tập tin, nhưng biết chắc ứng dụng khác có thể mở được tập tin đó, hãy giữ Command + Alt rồi kéo tập tin thả vào biểu tượng tương ứng của ứng dụng (có thể kéo vào biểu tượng cả trên Dock hay trong Finder tùy ý). Thao tác này buộc ứng dụng bạn chọn phải mở tập tin đó; thậm chí có thể ép bất cứ ứng dụng nào thử mở tập tin nào đó kể cả khi chúng không phải là “bạn”.Một phương án khác là bạn nhấn chuột vào tập tin cần mở, nhấn phải chuột rồi chọn Get Info (hay dùng tổ hợp phím Command + I), sau đó chọn Open With và chỉ định ứng dụng từ menu thả xuống. Bạn có thể thiết lập ứng dụng trở thành mặc định để mở các tập tin cùng loại bằng cách nhấn nút Change All.")
        giupMacData.addData("Dọn trống ổ cứng và tăng bộ nhớ RAM", imagePath: "Dontrong.jpg", detail: "Sau một thời gian sử dụng, nếu bạn cảm thấy Mac trở nên chậm chạp và đòi hỏi nhiều thời gian hơn để hoàn thành các tác vụ cơ bản, hãy thử kiểm tra ổ cứng xem có còn chỗ trống (mở cửa sổ Get Info như đề cập trong mục 3). Nếu khoảng trống còn ít hơn 20%, bạn nên tiến hành dọn dẹp.Trước tiên, bạn hãy kiểm tra thùng rác của OS X (Trash). Thực tế có nhiều người dùng chuyển từ Windows qua Mac, vô tình bỏ qua việc OS X chỉ có thể chuyển tập tin vào thùng rác khi bạn xóa trong Finder chứ không bỏ hẳn như Explorer của Windows. Rất nhiều người ngạc nhiên khi sau một vài tháng sử dụng, ổ cứng chỉ còn… 0 byte trống và nghĩ rằng Mac có vấn đề.Bên cạnh việc dọn Trash, các thủ thuật thông thường như ghi ra đĩa quang, thay ổ cứng, mua thêm ổ lắp ngoài… cũng là điều nên làm. Bản thân các ứng dụng iPhoto, iTunes… cũng có cơ sở dữ liệu khá lớn. Ngoài việc chuyển bớt các đối tượng ra ngoài, bạn thậm chí có thể chuyển cả thư viện ra ổ ngoài (dĩ nhiên cần một số thủ thuật nhỏ).Cuối cùng, xem xét nâng cấp RAM hết mức có thể. Với OS X hiện tại, mức 4GB là khá đủ (không muốn nói là dư thừa) với các ứng dụng. Nếu thường xuyên xử lý phim ảnh hay thiết kế 3D, bạn có thể cần dung lượng 8GB trở lên. ")
        giupMacData.addData("Tìm tập tin thất lạc", imagePath: "Timtap.jpg", detail: "Hầu hết người dùng đều có xu hướng sắp xếp các tài liệu vào nhiều thư mục khác nhau trên Mac. Tuy nhiên, sau một thời gian, khi số lượng tập tin cũng như thư mục đã vượt ngưỡng vài ngàn thì việc tìm lại một đối tượng nằm đâu đó trở nên… vô phương. Nhưng may mắn là tính năng tìm kiếm Spotlight của OS X là hữu hiệu. Biểu tượng của nó có mặt ở nhiều vị trí trong OS X (thông thường nằm ở góc phải phía trên màn hình). Bạn nhấn vào đó và nhập thông tin (có thể là một phần tên tập tin hay nội dung bên trong) rồi sử dụng các bộ lọc (giới hạn khu vực tìm, chủng loại tập tin…). Trong Finder, bạn có thể dùng tổ hợp Command + F để kích hoạt lệnh tìm kiếm, trong khi Command + Spacebar (nút cách) sẽ mở Spotlight từ bất kì đâu. Ngoài việc tìm kiếm, Spotlight tiện trong các tác vụ khác như khởi động nhanh ứng dụng, tính toán, tìm kiếm tổng thể. Thậm chí nó còn có thể tra luôn từ điển bằng cách thực hiện mệnh lệnh tìm kiếm trong các cơ sở dữ liệu của ứng dụng…")
        giupMacData.addData("Khôi phục mật khẩu quản trị ", imagePath: "Khoiphuc.jpg", detail: "Do đặc tính bảo mật cao, OS X yêu cầu người dùng nhập lại mật khẩu quản trị bất cứ khi nào có phần mềm cài đặt vào hệ thống hay cập nhật các ứng dụng. Nếu bạn quên... rắc rối sẽ xảy ra. Dĩ nhiên việc mò mẫm thử nhập các mật khẩu “na ná” để nhớ lại mật khẩu chính xác có thể hiệu quả, nhưng Táo đã cung cấp sẵn một tiện ích cho phép khôi phục mật khẩu quản trị. Quy trình như sau:\n Bạn cho đĩa(usb) cài OS X vào ổ quang( cắm vô cổng usb) của máy rồi khởi động lại. \n Giữ phím Alt khi máy khởi động lại để mở menu khởi động, chọn ổ quang tương ứng.\n Trong môi trường cài đặt của OS X, bạn chọn Utilities > Reset Password từ thanh công cụ và nhập vào một mật khẩu mới. \n Khởi động lại máy từ ổ cứng như bình thường và mật khẩu mới sẽ bắt đầu có tác dụng.")
        giupMacData.addData("Khôi phục kết nối Wifi bị gián đoạn ", imagePath: "Khoiphucketnoi.jpg", detail: "Không gì khó chịu bằng việc bạn không thể truy cập mạng không dây cho dù đã thử đủ mọi cách. Nếu kết nối Wifi đột ngột bị ngắt, hãy kiểm tra xem có vô tình tắt Airport của máy hay không. Ngoài ra, bạn cũng nên vào System Preferences > Network, mở AirPort > TCP/IP rồi nhấn vào Renew DHCP Lease để xem lỗi có biến mất hay không. Dĩ nhiên, bạn luôn đảm bảo đang ở trong khoảng sóng Wifi đủ mạnh để sử dụng. Biểu tượng Airport trên thanh công cụ của OS X sẽ báo hiệu điều này bằng các vạch thông báo. Đôi khi, việc tắt Airport và bật lại cũng có thể giải quyết được vấn đề kết nối vì nhiều ứng dụng lúc kết nối hay thậm chí chính công cụ Airport của OS X cũng có thể gặp trục trặc.")
